import React from 'react';
import styles from './Hero.module.css';
import { motion } from 'framer-motion';
import Image from 'next/image';
import Menu from './Menu';
import { HiArrowCircleDown } from 'react-icons/hi';

const yAxisHandsAnimation = {
  initial: { y: 650 },
  animate: { y: 1, rotate: 360, transition: { duration: 1 } },
};
const opacityAnimation = {
  initial: { opacity: 0 },
  animate: { opacity: 1, transition: { duration: 0.3 } },
};
//Animation Config
const yFloatingAnimation = {
  static: { y: -70, opacity: 0 },
  floating: {
    y: [-60, -50],
    transition: {
      duration: 1.8,
      ease: 'easeInOut',
      yoyo: Infinity,
    },
  },
};
const Hero = () => {
  return (
    <section id="hero">
      <div className={styles.heroContainer}>
        <Menu />
        <div className={styles.hero}>
          <motion.h1
            variants={opacityAnimation}
            initial="initial"
            animate="animate"
            transition="transition"
            className={styles.title}
          >
            Strawberry Feverâ„¢
          </motion.h1>
          <motion.div
            variants={opacityAnimation}
            initial="initial"
            animate="animate"
            transition="transition"
            className={styles.imageAndText}
          >
            <div className={styles.text}>
              <h2>
                Introducing
                <br />
                Our New Flavor!
              </h2>
              <p>
                Developed by scientist from the DoD and NASA, we bring something
                new, something transformative In fact when's the last time you
                had a passionate cry? Go ahead, take out that Home Equity Line
                of Credit, because you'll never get enough.
              </p>
              <button>Learn More</button>
            </div>
            <motion.div
              variants={yFloatingAnimation}
              initial="static"
              animate="floating"
              transition="transition"
              className={styles.heroImageContainer}
            >
              <div className={styles.image}>
                <Image
                  src="/straw.png"
                  alt="strawberry ice cream cone"
                  width={297}
                  height={540}
                />
              </div>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
