import React from 'react';
import styles from './Hero.module.css';
import { motion } from 'framer-motion';
import Image from 'next/image';
const yAxisHandsAnimation = {
  initial: { y: 650 },
  animate: { y: 1, transition: { duration: 1, } },
};
const yAxisTitleAnimation = {
  initial: { y: -50 },
  animate: { y: 1, transition: { duration: 1,  } },
};
//Animation Config
const yFloatingAnimation = {
  static: { y: 0 },
  floating: {
    y: [40, -40],
    transition: {
      duration: 1.8,
      ease: 'easeInOut',
      yoyo: Infinity,
    },
  },
};
const Hero = () => {
  return (
    <div className={styles.hero}>
      <motion.h1
        variants={yAxisTitleAnimation}
        initial="initial"
        animate="animate"
        transition="transition"
      >
        Introducing Strawberry Fever!â„¢
      </motion.h1>
      <motion.div
        variants={yFloatingAnimation}
        initial="static"
        animate="floating"
        transition="transition"
        className={styles.heroImageContainer}
      >
        <Image
          src="/straw.png"
          alt="three hands holding ice cream cones"
          width={1150}
          height={800}
        />
      </motion.div>
    </div>
  );
};

export default Hero;
