import React from 'react';
import styles from './Hero.module.css';
import { motion } from 'framer-motion';
import Image from 'next/image';
import Menu from './Menu';
import { HiArrowCircleDown } from 'react-icons/hi';

const yAxisHandsAnimation = {
  initial: { y: 650 },
  animate: { y: 1, rotate: 360, transition: { duration: 1 } },
};
const yAxisTitleAnimation = {
  initial: { y: -50 },
  animate: { y: 1, transition: { duration: 1 } },
};
//Animation Config
const yFloatingAnimation = {
  static: { y: 0 },
  floating: {
    y: [-5, -60],
    transition: {
      duration: 1.8,
      ease: 'easeInOut',
      yoyo: Infinity,
    },
  },
};
const Hero = () => {
  return (
    <div className={styles.heroContainer}>
      <Menu />
      <div className={styles.hero}>
        <motion.h1
          variants={yAxisTitleAnimation}
          initial="initial"
          animate="animate"
          transition="transition"
          className={styles.title}
        >
          Strawberry Fever!â„¢
        </motion.h1>
        <div className={styles.imageAndText}>
          <div className={styles.text}>
            <h2>
              Introducing
              <br />
              Our New Flavor!
            </h2>
            <p>
              You've had strawberry before, sure, not like this. Developed by
              scientist from the DoD and NASA, we bring something new, something
              transformative, mouth-watering, hair rai-.. In fact when's the
              last time you had a passionate cry? Go ahead, take out that Home
              Equity Line of Credit, because you'll never get enough.
            </p>
            <button>Learn More</button>
          </div>
          <motion.div
            variants={yFloatingAnimation}
            initial="static"
            animate="floating"
            transition="transition"
            className={styles.heroImageContainer}
          >
            <div className={styles.image}>
              <Image
                src="/straw.png"
                alt="strawberry ice cream cone"
                width={330}
                height={590}
              />
            </div>
          </motion.div>
        </div>
        <div className={styles.downArrow} aria-label="down arrow" role="button">
          <HiArrowCircleDown size={100} color="#ee4f91" />
        </div>
      </div>
    </div>
  );
};

export default Hero;
