import React, {useState, useEffect} from 'react';
import styles from './Info.module.css';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Wrapper from './Wrapper';
import Image from 'next/image';

const Info = () => {
  //Intersectional Observer Configurations
  const [ref, inView] = useInView({
    threshold: 0.3,
  });

  //Framer Animation Variants
  const loadOnEnter = {
    hidden: {
      opacity: 0
    },
    visible: {
      opacity: 1
    }
  }

  // Fix for breaking exit animation using Framer "AnimateControlsAPI"
  const [variant, setVariant] = useState("hidden");
  useEffect(() => {
    if (inView) {
      setVariant("visible");
    }
  }, [inView]);

  return (
    <AnimatePresence>
      <Wrapper ref={ref}>
        <div className={styles.information}>
          <div className={styles.iceCreamImage}>
            {inView ? (
              <motion.div
                variants={loadOnEnter}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                <Image
                  src="/shockedboy.png"
                  alt="surprised boy"
                  width={320}
                  height={350}
                />
              </motion.div>
            ) : (
              <div></div>
            )}
          </div>
          <div className={styles.infoTextContainer}>
            <div className={styles.infoText}>
              <div className={styles.thanks}>
                Thank You
                <br />-
              </div>

              <span className={styles.highlight}>
                <i>
                  “The best ice cream in the world, as anyone who has tried it
                  will argue...”
                </i>
              </span>
              <br />
              <div className={styles.magazine}>Time</div>
              <br />
            </div>
          </div>
        </div>
      </Wrapper>
    </AnimatePresence>
  );
};

export default Info;
