import React from 'react';
import styles from './Party.module.css';
import Image from 'next/image';
import { AnimatePresence, motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Wrapper from './Wrapper';

const Party = () => {
  //Intersectional Observer Configurations
  const [ref, inView] = useInView({
    threshold: 0.2,
    triggerOnce: false
  });
  return (
    <AnimatePresence>
      <Wrapper ref={ref}>
        <div className={styles.information}>
          <div className={styles.iceCreamImage}>
            {inView ? (
              <motion.div
                ref={ref}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                <Image
                  src="/partyhat.png"
                  alt="woman in party hat"
                  width={410}
                  height={460}
                  loading="lazy"
                />
              </motion.div>
            ) : (
              <div></div>
            )}
          </div>
          <div className={styles.infoTextContainer}>
            <div className={styles.infoText}>
              <div className={styles.thanks}>
                <br />-
              </div>

              <p>
                In order to secure more funding we've been advised to show at
                minimum 25% growth YoY!
                <br />
                <br />
                So we do parties now!
                <br />
                Isn't that great?
              </p>
              <br />
              <div className={styles.buttonsContainer}>
                <motion.button
                  role="button"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 1 }}
                >
                  Learn More
                </motion.button>
                <motion.button
                  role="button"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 1 }}
                >
                  Book Us
                </motion.button>
              </div>

              <div className={styles.magazine}>-</div>
              <br />
            </div>
          </div>
        </div>
      </Wrapper>
    </AnimatePresence>
  );
};

export default Party;
