import React from 'react';
import styles from './Banner.module.css';
import { motion } from 'framer-motion';
import { useInView, InView } from 'react-intersection-observer';

//Animation Config
const appearUpAnimation = {
  initial: { opacity: 0, y: 60 },
  animate: { opacity: 1, y: 1, transition: { delay: 0.5 } },
};
const yFloatingAnimation = {
  static: { y: -80, scale: 1 },
  floating: {
    scale: 1.1,
    y: [-60, -50],
    transition: {
      duration: 1.6,
      ease: 'easeInOut',
      yoyo: Infinity,
    },
  },
};
const Banner = () => {
  //Intersectional Observer Configurations
  const [ref, inView] = useInView({
    threshold: 0.3,
    triggerOnce: true,
  });
  return (
    <div ref={ref} className={styles.information}>
      {inView ? (
        <motion.div
          className={styles.infoTextContainer}
          variants={appearUpAnimation}
          initial="initial"
          animate="animate"
          transition="transition"
        >
          <motion.div className={styles.infoText}>
            <motion.h1
              variants={yFloatingAnimation}
              initial="static"
              animate="floating"
              transition="transition"
            >
              "GREEN with ENVY"
            </motion.h1>
            <h4>2021</h4>
          </motion.div>
        </motion.div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default Banner;
