import React from 'react';
import styles from './Banner.module.css';
import { motion } from 'framer-motion';
import { useInView, InView } from 'react-intersection-observer';

//Animation Config
const appearUpAnimation = {
  initial: { opacity: 0, y: 60 },
  animate: { opacity: 1, y: 1, transition: { delay: 0.5 } },
};
const Banner = () => {
  //Intersectional Observer Configurations
  const [ref, inView] = useInView({
    threshold: 0.3,
    triggerOnce: true,
  });
  return (
    <div       className={styles.information}>
      <InView  ref={ref}>
        {inView ? (
          <motion.div
            className={styles.infoTextContainer}

            variants={appearUpAnimation}
            initial="initial"
            animate="animate"
            transition="transition"
          >
            <motion.div className={styles.infoText}>
              <motion.h1>"GREEN with ENVY"</motion.h1>
              <h4>2021</h4>
            </motion.div>
          </motion.div>
        ) : (
          <div></div>
        )}
      </InView>
    </div>
  );
};

export default Banner;
