import React from 'react';
import styles from './Menu.module.css';
import {
  AiFillTwitterCircle,
  AiFillFacebook,
  AiFillInstagram,
} from 'react-icons/ai';
import { RiFacebookCircleFill } from 'react-icons/ri';

import Image from 'next/image';
import { motion } from 'framer-motion';

const animateAppearUp = {
  initial: {
    opacity: 0,
    y: 10,
  },
  animate: {
    opacity: 1,
    y: 0,
  },
  transition: {
    delay: 10,
  },
};
const Menu = () => {
  return (
    <div className={styles.welcomeMenu}>
      <div className={styles.menu}>
        <div className={styles.navigation} role="navigation">
          <motion.div
            variants={animateAppearUp}
            animate="animate"
            initial="initial"
            transition={{ delay: 0.5 }}
            className={styles.menuSectionA}
          >
            <span className={styles.storeName}>Scoops!</span>
            <div className={styles.navigationLinksA}>
              <div className={styles.navigationItem}>
                <motion.a
                  whileHover={{
                    color: '#EE4F91',
                  }}
                  href="#"
                >
                  <span aria-label="restaurant menu">Menu</span>
                </motion.a>
              </div>
              <div className={styles.navigationItem}>
                <motion.a
                  whileHover={{
                    color: '#EE4F91',
                  }}
                  href="#"
                >
                  Waitlist
                </motion.a>
              </div>
            </div>
          </motion.div>
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            whileHover={{ rotate: 360, transition: { duration: 0.5 } }}
            className={styles.logoContainer}
          >
            <div className={styles.logo}>
              <div>
                <Image src="/icelogo.svg" alt="logo" width={300} height={300} />
              </div>
            </div>
          </motion.div>
          <motion.div
            variants={animateAppearUp}
            animate="animate"
            initial="initial"
            transition={{ delay: 0.5 }}
            className={styles.menuSectionB}
          >
            <div className={styles.navigationItem}>
              <motion.a
                whileHover={{
                  color: '#EE4F91',
                }}
                href="#"
              >
                Booking
              </motion.a>
            </div>
            <div className={styles.socialMediaIcons}>
              <motion.a
                whileHover={{
                  color: '#EE4F91',
                }}
                href="#"
              >
                Visit
              </motion.a>
              <RiFacebookCircleFill size={30} />
              <AiFillInstagram size={30} />
              <AiFillTwitterCircle size={30} />
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default Menu;
